import React from 'react';

const styles = {
    image: {
        width: '25%',
        minWidth: '250px',
        height: '25%',
        minHeight: '250px',
        borderRadius: '25%',
        borderColor: 'black',
        borderWidth: '5px',

    },
    ptag: {
        fontSize: '25px',
        fontFamily: 'fantasy',
        fontWeight: '940',
        lineHeight: '26px',
        marginBottom: '-8%'
    },
    ptag2: {
        fontSize: '25px',
        fontFamily: 'fantasy',
        fontWeight: '940',
        lineHeight: '30px',
    },
    grey: {
        color: 'slategrey'
    }
}

var text = document.getElementById('text');
var newDom = '';
var animationDelay = 6;

for(let i = 0; i < text.innerText.length; i++)
{
    newDom += '<span class="char">' + (text.innerText[i] == ' ' ? '&nbsp;' : text.innerText[i])+ '</span>';
}

text.innerHTML = newDom;
var length = text.children.length;

for(let i = 0; i < length; i++)
{
    text.children[i].style['animation-delay'] = animationDelay * i + 'ms';
}


const About = () => {

    return (

        <>
            <div className='portfoliocontainer'>
                <div className="aboutcontainer">
                    <div className="aboutleftside">
                        <img src='https://avatars0.githubusercontent.com/u/54956992?s=460&v=4' alt='picture' style={styles.image} />
                        <h1 style={styles.grey}>.</h1>
                    </div>
                    <div className="aboutrightside">
                        <center>
                            <div class="typewriter">
                                <p style={styles.ptag}>I am a Front-End Developer...</p>
                            </div>
                            <p style={styles.ptag2}>I enjoy creating responsive designs and have the ability to communicate well with team members to minimize debugging and downtime. Some of my skills include mobile first design, concentrated Javascript, and client/server logic. </p>
                            <p style={styles.ptag2}>Core Compentencies: HTML/CSS, JavaScript, jQuery, React, Redux, MySQL, MongoDB, Node, Express, Git, Bash</p>
                            </center>
                            <h2>Escape</h2> 
<h2> 
  <h4>into amazing experiences</h4>
</h2>
                    </div>
                </div>

            </div>
        </>
    )
}
export default About
